import React, { Component } from "react";
import './SearchBox.css';

import { Link } from 'react-router-dom';

class SearchBox extends Component {
  constructor() {
    super();
    this.state = {
      user:''
    }; 
    this.teste = this.teste.bind(this); 
  }

  teste(e) {
    return
    <h1>e</h1>
  }

  render() {
    const searchBar = {
      logo: <img src="../images/logo.png" alt="Imagem do logotipo do mercado livre." />,
      lupa: <img className="img-lupa" src="../images/lupa.png" alt="Imagem de uma lupa na barra de pesquisa." />,
      input: <input type="search" name="search" placeholder="Buscar produtos, marcas e muito mais..." />
    };

    return (
      <>
        <div className='search-box'>
          {searchBar.logo}
          <div className='search-input-container'>
          <Link to='/item?search='>
            <form action="" className='input-field'>
              {searchBar.input}              
              < i class="fas fa-search" onChange={ e => this.teste(e)}></i> 
            </form>
              </Link>                  
          </div>
        </div>
      </>
    );
  }
}

export default SearchBox;
