import React, { Component } from "react";
import lupa from '../images/lupa.png';


class SearchBox extends Component {
  constructor() {
    super();
  }

  render() {
    const lupa = <img src='../images/lupa.png' alt='lupa'/>

    return (
      <>
        <form action="">
          <div>
            <input
              type="search"
              name="search"
              placeholder="Buscar produtos, marcas e muito mais... "
            />            
            {lupa}
            <img src={lupa} alt="Imagem de uma lupa na barra de pesquisa."/>
          </div>
        </form>
      </>
    );
  }
}

export default SearchBox;
