import React, { Component } from "react";
import './SearchBox.css';

import { Link } from 'react-router-dom';

class SearchBox extends Component {
  constructor() {
    super();
    this.state = ''; 
  }

  render() {
    const searchBar = {
      logo: <img src="../images/logo.png" alt="Imagem do logotipo do mercado livre." />,
      lupa: <img className="img-lupa" src="../images/lupa.png" alt="Imagem de uma lupa na barra de pesquisa." />,
      input: <input type="search" name="search" placeholder="Buscar produtos, marcas e muito mais..." />
    };

    return (
      <>
        <div className='search-box'>
          {searchBar.logo}
          <div className='search-input-container'>
            <form action="" className='input-field'>
              {searchBar.input} 
              <Link to="/item">
                <dir className="img-lupa">
                <svg focusable="false" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0 0 16 9.5 6.5 6.5 0 1 0 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"></path></svg>
                </dir>
              </Link>



            </form>
          </div>
        </div>
      </>
    );
  }
}

export default SearchBox;
